&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;collection父接口&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#collection父接口&#34;&gt;#&lt;/a&gt; Collection 父接口&lt;/h2&gt;
&lt;p&gt;特点：代表一组任意类型的对象，无序，无下标、不能重复。&lt;/p&gt;
&lt;p&gt;方法：&lt;/p&gt;
&lt;p&gt;使用：&lt;/p&gt;
&lt;p&gt;​&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;public class demo1 &amp;#123;
    public static void main(String[] args) &amp;#123;
        //创建集合
        Collection collection = new ArrayList();
        //(1)添加元素
        collection.add(&amp;quot;苹果&amp;quot;);
        collection.add(&amp;quot;西瓜&amp;quot;);
        System.out.println(&amp;quot;元素个数：&amp;quot;+collection.size());
        System.out.println(collection);

        //(2)删除元素
        //collection.remove(&amp;quot;苹果&amp;quot;);
        //collection.clear();//清空

        //(3)遍历元素 （重点）
        //3.1增强for
        System.out.println(&amp;quot;========3.1增强for=========&amp;quot;);
        for (Object object:collection
             ) &amp;#123;
            System.out.println(object);

        &amp;#125;
        //3.2 使用迭代器（Iterator迭代器专门用来遍历集合的一种方式）
        //hasNext();有没有下一个元素
        //next();获取下一个元素
        //remove();删除当前元素
        System.out.println(&amp;quot;========3.2 使用迭代器=========&amp;quot;);
        Iterator it = collection.iterator();
        while (it.hasNext())&amp;#123;
            String s = (String)it.next();
            System.out.println(s);
            //在迭代过程中不能使用collection的删除方法
            //collection.remove(s);
            it.remove();
        &amp;#125;

        //(4)判断
        System.out.println(collection.contains(&amp;quot;苹果&amp;quot;));
        System.out.println(collection.isEmpty());
    &amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
